import { useEffect, useState } from 'react';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import axios from 'axios';

function ProductSection() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/products') // change URL as needed
      .then(response => {
        // Sort by ID or date and take the latest 8
        const latestProducts = response.data
          .sort((a, b) => b.id - a.id) // Or use b.date - a.date
          .slice(0, 8);
        setProducts(latestProducts);
      })
      .catch(error => {
        console.error('Error fetching products:', error);
      });
  }, []);

  return (
    <section style={{
      height: "auto",
      display: 'flex',
      flexDirection: "column",
      gap: "5rem",
      justifyContent: "center",
      textAlign: "center",
      marginTop: "30px",
      marginBottom: "30px"
    }}>
      <h1>Latest Products</h1>
      <div style={{
        height: 'auto',
        display: 'flex',
        flexWrap: 'wrap',
        gap: "3rem",
        justifyContent: "center"
      }}>
        {products.map(product => (
          <Card key={product.id} style={{ width: '18rem' }}>
            <Card.Img variant="top" src={product.imageUrl} />
            <Card.Body>
              <Card.Title>{product.title}</Card.Title>
              <Card.Text>{product.description}</Card.Text>
              <Card.Text><strong>{product.price}</strong></Card.Text>
              <Button variant="primary" href={product.buyLink} target="_blank">Buy Now</Button>
            </Card.Body>
          </Card>
        ))}
      </div>
    </section>
  );
}

export default ProductSection;
